//by default take the properties configured in the ui
var ledColorRGBHex = env.get("ledColorRGBHex")
var sleepDelayMs = env.get("sleepDelayMs")
//override if properties exist in the msg
if (msg.payload.ledColorRGBHex !== undefined) {
    ledColorRGBHex = msg.payload.ledColorRGBHex
}

if (msg.payload.sleepDelayMs != undefined) {
    sleepDelayMs = msg.payload.sleepDelayMs 
}
//convert payload
const payload = {
 "ledColorRGBHex": ledColorRGBHex,
 "sleepDelayMs": sleepDelayMs
}
msg.payload = JSON.stringify(payload)
//return our new constructed message
return msg
